import React from 'react'
import { StyleSheet, Text, View } from 'react-native'
import { MaterialCommunityIcons as Icon } from 'react-native-vector-icons'

class App extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      gameState: 
      [
        [0, 0, 0],
        [0, 0, 0],
        [0, 0, 0]
      ]
  ,
      currentPlayer: 1,
    }
  }

  componentDidMount() {
    this.initializeGame()
  }

  initializeGame = () => {
    this.setState({gameState: 
      [
        [1, -1, 0],
        [0, 0, 0],
        [0, 0, 0]
      ]
    })
  }

  renderIcon = (row, col) => {
    let value = this.state.gameState[row][col]
    switch(value) {
      case 1: return  <Icon name="close" style={styles.tileX} /> 
      case 2: return  <Icon name="circle-outline" style={styles.tile0} />
      default: <View></View>
    }
  }

  render() {
    return (
      <View style={styles.container}>

        <View style={{flexDirection: "row"}}>
          <View style={[styles.tile, { borderLeftWidth: 0, borderTopWidth: 0}]} >
           {this.renderIcon(0, 0)}
          </View>
          <View style={[styles.tile, { borderTopWidth: 0}]} >
            {this.renderIcon(0, 1)}  
          </View>
          <View style={[styles.tile, { borderTopWidth: 0, borderRightWidth: 0}]} />
        </View>
        
        <View style={{flexDirection: "row"}}>
          <View style={[styles.tile, { borderLeftWidth: 0}]} />
          <View style={[styles.tile]} />
          <View style={[styles.tile, { borderRightWidth: 0}]} />
        </View>
        
        <View style={{flexDirection: "row"}}>
          <View style={[styles.tile, { borderLeftWidth: 0, borderBottomWidth: 0}]} />
          <View style={[styles.tile, { borderBottomWidth: 0}]} />
          <View style={[styles.tile, { borderBottomWidth: 0, borderRightWidth: 0}]} />
        </View>
        

      </View>
    )
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  tile: {
    borderWidth: 5, 
    borderColor: '#007580',
    width: 100,
    height: 100,
  },
  tileX: {
    color: '#e9896a',
    fontSize: 60,
    paddingLeft: 10
  },
  tile0: {
    marginLeft: 10,
    color: '#96bb7c',
    fontSize: 60,
  }
})

export default App
